<?php/** * After content template. * As default this load after_singular hook and comments template. * * @package Shell * @subpackage Template */if ( is_singular() ){	/**	 * "shell_after_singular" (atomic action hook)	 * sidebar-after-singular.php is loaded in this hook	 * 	 * @see shell_get_sidebar_after_singular()	 * @since 0.1.0	 */	do_atomic( 'after_singular' ); // shell_after_singular	comments_template( '/comments.php', true ); // Loads the comments.php template.} ?>